import { useEffect, useState, useRef } from "react";

import "./Main.scss";

const Main = () => {
    const [isOpen, setOpen] = useState(true);
    const sheet = useRef();
    const videoRef = useRef(null);

    useEffect(() => {
        if (!videoRef) return
        navigator.mediaDevices.getUserMedia({ video: true }).then(stream => {
            let video = videoRef.current;
            video.srcObject = stream;
            video.play();
        });
    }, [videoRef]);

    return (
        <div className="main">
            <div className="main__sheet">
            <Sheet
                style={{ padding: 20 }}
                ref={sheet}
                isOpen={isOpen}
                snapPoints={[150, window.innerHeight * 0.8]}
                onClose={() => setOpen(false)}
            >
                <Sheet.Container>
                    <Sheet.Header>
                        <Sheet.Content>
                            Hello World
                        </Sheet.Content>
                    </Sheet.Header>
                </Sheet.Container>
            </Sheet>
            </div>

            <video
                width={window.innerWidth}
                height={window.innerHeight}
                ref={videoRef}
            />
        </div>
    );
};

export default Main;
