import { useEffect, useState, useRef } from "react";
import StarIcon from '@mui/icons-material/Star';
import LanguageIcon from '@mui/icons-material/Language';
import LocalPhoneIcon from '@mui/icons-material/LocalPhone';
import QueryBuilderIcon from '@mui/icons-material/QueryBuilder';
import LocationOnIcon from '@mui/icons-material/LocationOn';

import "./Main.scss";

const Main = () => {
    const [isOpen, setOpen] = useState(true);
    const sheet = useRef();
    const videoRef = useRef(null);

    useEffect(() => {
        if (!videoRef) return
        navigator.mediaDevices.getUserMedia({ video: true }).then(stream => {
            let video = videoRef.current;
            video.srcObject = stream;
            video.play();
        });
    }, [videoRef]);

    return (
        <div className="main">
            <div className="display">
                <div className="sheet">
                    <div className="handle">
                        <div className="knob" />
                    </div>
                    <div className="title-row">
                        <div className="title">McDonald's</div>
                        <div className="rating">
                            <div className="score">4.5</div>
                            <StarIcon className="star-icon" />
                        </div>
                    </div>
                    <div className="description-row">
                        <div className="place-type">
                            <div className="type">Restaurant</div>
                            <div className="cost"> . $$</div>
                        </div>
                        <div className="place-status">Open</div>
                    </div>
                    <div className="info">
                        <LanguageIcon className="icon" />
                        <div className="url">mcdonalds.com</div>

                    </div>
                    <div className="info">
                        <LocalPhoneIcon className="icon" />
                        <div className="number">514-697-2799</div>
                    </div>
                    <div className="info">
                        <QueryBuilderIcon className="icon" />
                        <div className="closing">Closes 5 pm</div>
                    </div>
                    <div className="info">
                        <LocationOnIcon className="icon" />
                        <div className="address">17000 Trans-Canada Hwy, Kirkland, Quebec H9J 2M5, Canada</div>
                    </div>
                </div>
            </div>

            <video
                width={window.innerWidth}
                height={window.innerHeight}
                ref={videoRef}
            />
        </div>
    );
};

export default Main;
